mapper.select:

select 

,

from QRTZ_TRIGGERS
where TRIGGER_GROUP = #{triggerGroup,jdbcType=VARCHAR}
  and TRIGGER_NAME = #{triggerName,jdbcType=VARCHAR}

mapper.delete:

delete from QRTZ_TRIGGERS
where TRIGGER_GROUP = #{triggerGroup,jdbcType=VARCHAR}
  and TRIGGER_NAME = #{triggerName,jdbcType=VARCHAR}
